mapper.select:
select t.* from ( SELECT a.id as ids,a.create_date AS join_date,a.medical_number as medical_numbers,a.patient_id as patient_id, a.doctor_id as doctor_id,a.ward_id,a.bed_id,a.care_level as care_level,a.number as number,a.nurse_id as nurse_id, b.id,b.hosptal_regist_id,b.is_discharged,b.create_date,b.medical_number,b.department_id as department_id, c.name AS name, e.name AS names,e.age AS ages,e.idcard AS idcards,e.sex AS sexs,e.phonenumber as phone_number, case when a.medical_number is null then 1 else 2 end as is_re, case when b.id is null and a.is_fail is null then 1 when b.id is null and a.is_fail = 3 then 2 else 3 end as is_hos FROM his_hosptalregist AS a LEFT JOIN his_hospital_manage AS b ON b.hosptal_regist_id = a.id LEFT JOIN his_doctor_info AS c ON c.id = a.doctor_id LEFT JOIN his_nurse AS d ON d.id = a.nurse_id LEFT JOIN his_patient_info AS e ON e.id =a.patient_id left join his_ward as f on f.id = a.ward_id left join his_bed as g on g.id = a.bed_id WHERE 1=1
and e.name like concat('%' #{parameter.names,jdbcType=VARCHAR},'%')
and e.idcard like concat('%' #{parameter.idcards,jdbcType=VARCHAR},'%')
and a.number like concat('%' #{parameter.number,jdbcType=VARCHAR},'%')
and a.department_id = #{parameter.departmentId,jdbcType=BIGINT}
and is_discharged = #{parameter.isDischarged,jdbcType=INTEGER}
) as t where 1 =1
and is_re = #{parameter.isRe,jdbcType=INTEGER}
and is_hos = #{parameter.isHos,jdbcType=INTEGER}
order by ${parameter.orders}

select his_hospital_manage .*
from his_hospital_manage
where hosptal_regist_id = #{id,jdbcType=BIGINT}

SELECT a.*, b.name as doctorName,c.name as nurseNames,d.number as wardsNumber,e.number as bedsNumber FROM his_hospital_manage as a left join his_user_doctor as b1 on a.doctor_id = b1.user_id left join his_doctor_info as b on b1.doctor_id = b.id left join his_user_nurse as c1 on a.nurse_id = c1.user_id left join his_nurse as c on c1.nurse_id = c.id left join his_ward as d on a.ward_id = d.id left join his_bed as e on a.bed_id = e.id where 1= 1
and a.is_discharged =#{parameter.isDischarged,jdbcType=VARCHAR}
and a.medical_number like concat('%' #{parameter.medicalNumber,jdbcType=VARCHAR},'%')
and a.number like concat('%' #{parameter.number,jdbcType=VARCHAR},'%')
and a.idcard like concat('%' #{parameter.idcard,jdbcType=VARCHAR},'%')
and a.phone_number like concat('%' #{parameter.phoneNumber,jdbcType=BIGINT},'%')
and a.patient_name like concat('%' #{parameter.patientName,jdbcType=VARCHAR},'%')
and d.number = #{parameter.wardsNumber,jdbcType=INTEGER}
and e.number = #{parameter.bedsNumber,jdbcType=INTEGER}
and b.name like concat('%' #{parameter.doctorName,jdbcType=VARCHAR},'%')
and c.name like concat('%' #{parameter.nurseNames,jdbcType=VARCHAR},'%')
order by ${parameter.orders}

