mapper.select:
select distinct a.record_id ,a.hosptalregist_number ,a.patient_id ,a.nurse_id ,a.number ,a.start_time ,a.create_date ,c.name as nurseName from his_infusion as a LEFT JOIN his_user_nurse as b on b.user_id = a.nurse_id LEFT JOIN his_nurse as c on b.nurse_id = c.id LEFT JOIN his_medication_details as d on a.drugs_numb = d.id where a.hosptalregist_number = #{parameter.hosptalregistNumber,jdbcType = VARCHAR}
and a.type = 1
and a.type = 2
GROUP BY number
order by ${parameter.orders}

    select his_infusion.*
from his_infusion
where his_infusion.number =#{infusionNumber,jdbcType=VARCHAR}

select his_infusion.*
from his_infusion
where his_infusion.remarks =#{parameter.remarks,jdbcType=VARCHAR}

  order by ${parameter.orders}

mapper.update:
update his_infusion
set remarks = #{remarks,jdbcType=VARCHAR}
where number = #{number,jdbcType=BIGINT}

mapper.select:
    select his_infusion.*
from his_infusion
where medication_id = #{id.jdbcType=BIGINT}
