mapper.select:
    select his_medi_exit_details.*
    FROM his_medi_exit_details
    where record_number =#{recordNumber,jdbcType=VARCHAR}
      and drugs_numb=#{drugsNumb,jdbcType=VARCHAR} and validity_period =#{validityPeriod,jdbcType=TIMESTAMP}
order by his_medi_exit_details.create_date desc

select m.id ,e.toll_number, e.drugs_name,e.drugs_spec,e.exit_count,e.create_date,i.user_name,e.record_number,o.`name` as patient_Name,e.validity_period from his_medi_exit_details as e LEFT JOIN his_medicine_info as m on m.drugs_numb = e.drugs_numb LEFT JOIN `his-userinfo`.user_info as i on i.id = e.create_user_id LEFT JOIN his_medical_record as r on r.medical_record_id = e.record_number LEFT JOIN his_patient_info as o on o.id =r.patient_id where e.id in (select r.exit_id from his_related_medicationandexit as r where r.medication_detail_id in ( select d.id from his_medication_details as d where d.medical_record_id = (
select distinct h.medical_record_id from his_medication_details as h where h.id in
#{id,jdbcType=BIGINT}
) and d.id not in #{id,jdbcType=BIGINT} ) )

select d.*,i.user_name from his_medi_exit_details as d LEFT JOIN `his-userinfo`.user_info as i on i.id = d.create_user_id where 1=1
and d.toll_number like concat('%' #{parameter.tollNumber},'%')
order by ${parameter.orders}
