mapper.select:
select
activity_personnel_info.*
from activity_personnel_info
where
activity_personnel_info.activity_id=#{parameter.activityId,jdbcType=BIGINT}

  and activity_personnel_info.create_date >= #{parameter.createDate,jdbcType=TIMESTAMP}


  and activity_personnel_info.create_date <= #{parameter.createLowDate,jdbcType=TIMESTAMP}


  order by ${parameter.orders}
