mapper.select:
 select

 from site
where 1=1

   and site_name like concat('%', #{parameter.siteName,jdbcType=VARCHAR},'%')


   and location  like concat('%', #{parameter.location,jdbcType=VARCHAR},'%')


   and price >=  #{parameter.lowPrice,jdbcType=DECIMAL}


   and price <=  #{parameter.upPrice,jdbcType=DECIMAL}


   order by ${parameter.orders}

 update site
    set  is_enable = #{isEnable,jdbcType=SMALLINT}
		where id in

      #{id,jdbcType=BIGINT}
